package conditionals;

public class main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number = 24;


		if(number < 20) {
			System.out.println("Sayi 20'ten küçüktür");
		}else if(number == 20 ){
			System.out.println("Sayi 0'ye eşitti" );
		}else {
			System.out.println("Sayi 20'den büyüktür");
		}

	}

}
